/// <reference types="node" />
declare module "react-docgen" {
    interface Props {
        [key: string]: PropDescriptor;
    }
    interface ComponentDoc {
        displayName: string;
        description: string;
        props?: Props;
    }
    interface PreparedProp {
        name: string;
        value: Omit<PropDescriptor, "tsType" | "flowType">;
    }
    type PreparedComponentDoc = {
        componentName: string;
        description: string;
        props: PreparedProp[];
        hasChildren: boolean;
    };
    type PropTypeDescriptor = {
        name: "arrayOf" | "custom" | "enum" | "array" | "bool" | "func" | "number" | "object" | "string" | "any" | "element" | "node" | "symbol" | "objectOf" | "shape" | "exact" | "union" | "elementType";
        value?: any;
        raw?: string;
        computed?: boolean;
        description?: string;
        required?: boolean;
    };
    type FlowBaseType = {
        required?: boolean;
        nullable?: boolean;
        alias?: string;
    };
    type FlowSimpleType = FlowBaseType & {
        name: string;
        raw?: string;
    };
    type FlowLiteralType = FlowBaseType & {
        name: "literal";
        value: string;
    };
    type FlowElementsType = FlowBaseType & {
        name: string;
        raw: string;
        elements: Array<FlowTypeDescriptor>;
    };
    type FlowFunctionArgumentType = {
        name: string;
        type?: FlowTypeDescriptor;
        rest?: boolean;
    };
    type FlowFunctionSignatureType = FlowBaseType & {
        name: "signature";
        type: "function";
        raw: string;
        signature: {
            arguments: Array<FlowFunctionArgumentType>;
            return: FlowTypeDescriptor;
        };
    };
    type FlowObjectSignatureType = FlowBaseType & {
        name: "signature";
        type: "object";
        raw: string;
        signature: {
            properties: Array<{
                key: string | FlowTypeDescriptor;
                value: FlowTypeDescriptor;
            }>;
        };
    };
    type FlowTypeDescriptor = FlowSimpleType | FlowLiteralType | FlowElementsType | FlowFunctionSignatureType | FlowObjectSignatureType;
    type PropDescriptor = {
        type?: PropTypeDescriptor | FlowTypeDescriptor;
        flowType?: FlowTypeDescriptor;
        tsType?: FlowTypeDescriptor;
        required?: boolean;
        defaultValue?: any;
        description?: string;
    };
    function parse(src: string | Buffer, resolver?: string | unknown, handlers?: null, options?: {
        filename: string;
        babelrc: boolean;
    }): ComponentDoc | ComponentDoc[];
}
